  margin-inline-end: 2px !important;
}

:is(.browserContainer, #viewSource) .findbar-closebutton .toolbarbutton-icon {
  width: 16px !important;
  height: 16px !important;
}

:is(.browserContainer, #viewSource) .findbar-textbox {
  font-size: 14px !important;
}

:is(.browserContainer, #viewSource) :is(findbar, #FindToolbar) {
  -moz-box-ordinal-group: 0;
  /* can't be replace by box-ordinal-group yet */
  position: fixed !important;
  display: inherit !important;
  margin-top: -1px !important;
  box-shadow: unset !important;
  border: 1px solid #e1e1e2 !important;
  border-top: 0px solid transparent !important;
  border-radius: 0 0 3px 3px;
  transition-property: margin-top, opacity, visibility !important;
  background-image: unset !important;
  z-index: 1000000 !important;
}

#main-window:-moz-lwtheme:is(.browserContainer, #viewSource):is(findbar, #FindToolbar) {
  appearance: none !important;
  border: 1px solid var(--tabs-border-color) !important;
  border-top: 0px solid transparent !important;
}

#main-window:-moz-lwtheme:is(.browserContainer, #viewSource):is(findbar, #FindToolbar) {
  padding-block: 0 !important;
  border: unset !important;
}

#main-window:-moz-lwtheme:is(.browserContainer, #viewSource):is(findbar, #FindToolbar)
  .findbar-container {
  appearance: none !important;
  background-image: linear-gradient(
    to bottom,
    var(--toolbar-bgcolor),
    var(--toolbar-bgcolor)
  ) !important;
  margin-inline-end: -18px !important;
  padding-inline-end: 18px !important;
}

#main-window:-moz-lwtheme:is(.browserContainer, #viewSource):is(findbar, #FindToolbar)
  .findbar-container {
  appearance: none !important;
  background-image: linear-gradient(
    to bottom,
    var(--toolbar-bgcolor),
    var(--toolbar-bgcolor)
  ) !important;
  margin-inline-end: -26px !important;
  padding-inline-end: 26px !important;
  margin-inline-start: -26px !important;
  padding-inline-start: 26px !important;
}

#main-window:-moz-lwtheme:is(.browserContainer, #viewSource):is(findbar, #FindToolbar)
  .findbar-container
  hbox[anonid="findbar-textbox-wrapper"] {
  padding-inline-start: 8px;
}

:is(.browserContainer, #viewSource) :is(.findbar-find-status, .found-matches) {
  margin-inline-start: 4px !important;
  margin-inline-end: 4px !important;
  font-weight: bold !important;
}

:is(.browserContainer, #viewSource) .findbar-find-status {
  font-style: italic !important;
}

#main-window:not(:-moz-lwtheme-brighttext):is(.browserContainer, #viewSource)
  .findbar-find-status {
  color: red !important;
}

#main-window:not(:-moz-lwtheme-brighttext):is(.browserContainer, #viewSource)
  .found-matches {
  color: green !important;
}

#main-window:-moz-lwtheme-brighttext:is(.browserContainer, #viewSource):is(.findbar-find-status, .found-matches) {
  color: white !important;
}

.browserContainer > findbar > #findbar-close-container {
  background-color: unset !important;
}

/* checkbox to left */

hbox[anonid="findbar-textbox-wrapper"] {
  -moz-box-flex: 1 !important;
  -moz-box-ordinal-group: 0;
  box-align: end !important;
}

.findbar-container > :is(image, description, label) {
  -moz-box-ordinal-group: 0;
}

* {
  scrollbar-width: thin !important;
}

findbar {
  -moz-box-ordinal-group: 0;
  margin-bottom: -33px;
  position: relative;
  border-top: none !important;
  padding: 0 !important;
  transition: transform 82ms linear, opacity 82ms linear 32ms !important;
  background: none !important;
  pointer-events: none;
  z-index: 1;
}

.findbar-container > .findbar-find-fast {
  padding: var(--toolbarbutton-inner-padding) 1px;
  margin: 0 !important;
}

findbar[hidden] {
  transform: translateY(-30px);
}

findbar > .findbar-container,
findbar > .close-icon {
  border: 1px solid var(--chrome-content-separator-color);
  border-width: 0 0 1px 0px;
  background: var(--uc-light-bkgnd-color, var(--toolbar-bgcolor)) !important;
  pointer-events: auto;
}

findbar > .findbar-container {
  -moz-box-direction: reverse;
  border-left-width: 1px;
  border-bottom-left-radius: 4px;
}

.findbar-closebutton {
  margin: 0 !important;
  border-radius: 0 !important;
  padding: 5px !important;
}

.findbar-closebutton > image {
  padding: 3px;
}

.findbar-closebutton:hover > image {
  background: var(--toolbarbutton-hover-background) !important;
  border-radius: 4px;
}

findbar > .findbar-container > hbox {
  margin: 0 5px;
}

findbar::before {
  content: "";
  display: -moz-box;
  -moz-box-flex: 100;
}

