commits.content:
/** @format */

:root {
  /* All the CSS variables here are global */
  /* If windows - `-moz-win-glass`, if macOS - `-moz-mac-vibrancy-dark` */
  --bf-moz-appearance: -moz-mac-vibrant-titlebar-dark !important;
  --bf-backdrop-blur: 32px !important;
  /*6px*/
  --bf-sidebar-searchbar-radius: 9px !important;
  --bf-accent-bg: #4c5ff9cc;
  --bf-blank-page-bg: #252525;
  --bf-urlbar-hightlight-bg: var(--bf-accent-bg);
  --bf-urlbar-radius: 9px !important;
  --bf-urlbar-results-font-size: 12pt !important;
  --bf-urlbar-results-font-weight: 550 !important;
  --bf-urlbar-font-size: 11pt !important;
  --bf-urlbar-font-weight: 500 !important;
  --bf-urlbar-switch-tab-color: #6498ef !important;
  --bf-urlbar-bookmark-color: #53e2ae !important;
  --bf-navbar-padding: 6px !important;
  --bf-tab-selected-bg: #4d4dfeff;
  /*#985EFF88*/
  --bf-tab-font-size: 11pt !important;
  --bf-tab-font-weight: 400 !important;
  --bf-tab-height: 32px !important;
  --bf-tab-border-radius: 5px !important;
  /*--bf-tab-soundplaying-bg: #985EFFCC;*/
  --toolbar-bgcolor: transparent !important;
  --urlbar-separator-color: transparent !important;
}

/* Light Mode */

:root:-moz-lwtheme-darktext {
  --bf-main-window: transparent;
  --bf-bg: #f2f2f266;
  --bf-color: #0a0a0a;
  --bf-hover-bg: #1a1a1a33;
  --bf-active-bg: #1a1a1a66;
  --bf-icon-color: #0a0a0a;
  --bf-tab-toolbar-bg: #f2f2f2aa;
  --bf-tab-selected-bg: #00000022;
  --bf-navbar-bg: var(--bf-bg);
  --bf-urlbar-bg: var(--bf-bg);
  --bf-urlbar-active-bg: var(--bf-bg);
  --bf-urlbar-focused-color: var(--bf-color);
  --bf-sidebar-bg: var(--bf-bg);
  --bf-sidebar-color: var(--bf-color);
  --bf-menupopup-bg: #f2f2f2aa;
  --bf-menupopup-color: var(--bf-color);
}

/* Dark Mode */

:root:-moz-lwtheme-brighttext {
  --bf-main-window: transparent;
  --bf-bg: transparent;
  --bf-color: #fff;
  --bf-hover-bg: #f2f2f266;
  --bf-active-bg: #f2f2f266;
  --bf-icon-color: #f2f2f2;
  --bf-tab-toolbar-bg: #995effa4;
  --bf-tab-selected-bg: #985eff1a;
  --bf-navbar-bg: var(--bf-bg);
  --bf-urlbar-bg: var(--bf-bg);
  --bf-urlbar-active-bg: var(--bf-bg);
  --bf-urlbar-focused-color: var(--bf-color);
  --bf-sidebar-bg: var(--bf-bg);
  --bf-sidebar-color: var(--bf-color);
  --bf-menupopup-bg: #000000aa;
  --bf-menupopup-color: var(--bf-color);
}
