/* Center all content */

.tab-content {
  justify-content: center !important;
  align-items: center !important;
  margin-top: 0px !important;
  min-width: 100% !important;
  padding: 0 10px !important;
}

/* A way to center the label and icon while the close button is positioned to the far right */

.tab-content::before {
  content: "";
  display: -moz-box;
  -moz-box-flex: 1;
}

/* Tab close button */

.tab-close-button {
  opacity: 1 !important;
}

/* Prevent tab icons size breaking */

.tab-icon-image,
.tab-icon-sound,
.tab-throbber,
.tab-throbber-fallback,
.tab-close-button {
  min-width: 20px !important;
}

/* Adjust tab label width */

.tab-label-container {
  min-width: 16px !important;
}

/* If tab close button is not present, don't force favicon to the center */

#tabbrowser-tabs[closebuttons="activetab"]
  .tabbrowser-tab:not([selected="true"])
  .tab-throbber,
#tabbrowser-tabs[closebuttons="activetab"]
  .tabbrowser-tab:not([selected="true"])
  .tab-throbber-fallback,
#tabbrowser-tabs[closebuttons="activetab"]
  .tabbrowser-tab:not([selected="true"]):not([busy])
  .tab-icon-image,
#tabbrowser-tabs[closebuttons="activetab"]
  .tabbrowser-tab:not([selected="true"]):not([image])
  .tab-label-container {
  margin-left: 0 !important;
}

