#autohide-context {
  display: none !important;
}

#window-controls[hidden="true"] {
  display: none !important;
}

#window-controls[hidden="false"] {
  display: inline-flex !important;
}

/* Hide titlebar-buttonbox if there's single tab */

:root[tabsintitlebar][sizemode="maximized"]
