commits.content:
/* this is where we override firefox's built-in variables.
often we'll make our own variable in uc-globals, with a prefix like --uc-
and override a built-in variable to reference the new custom variable.
this way we get to share all the variables with content stylesheets.
otherwise you might want to use --arrowpanel-dimmed in a webextension popup,
but find that it's missing because docs do not inherit from <browser> elements. */

:root,
menupopup {
  --lwt-accent-color: var(--uc-accent-color) !important;
  --lwt-text-color: var(--uc-arrowpanel-color) !important;
  --lwt-toolbar-field-background-color: hsla(233, 25%, 28%, 85%) !important;
  --lwt-toolbar-field-color: hsl(240, 60%, 99%);
  --lwt-toolbar-field-focus: hsla(233, 25%, 28%, 85%) !important;
  --lwt-toolbar-field-focus-color: #fff !important;
  --toolbar-bgcolor: var(--uc-toolbar-bgcolor) !important;
  --toolbar-bgimage: none;
  --toolbar-color: var(--uc-toolbar-color) !important;
  --lwt-toolbarbutton-icon-fill: #fff !important;
  --lwt-toolbarbutton-icon-fill-attention: var(--attention-color) !important;
  --lwt-background-alignment: top !important;
  --lwt-background-tiling: repeat !important;
  --lwt-popup-brighttext-url-color: var(--urlbar-popup-action-color) !important;
  --toolbarbutton-border-radius: var(--general-button-border-radius) !important;
  --toolbarbutton-icon-fill-opacity: 1 !important;
  --arrowpanel-background: var(--uc-arrowpanel-background) !important;
  --arrowpanel-color: var(--uc-arrowpanel-color) !important;
  --arrowpanel-border-color: var(--uc-menu-border-color) !important;
  --arrowpanel-darker: var(--uc-arrowpanel-darker) !important;
  --arrowpanel-faint: var(--uc-arrowpanel-faint);
  --arrowpanel-dimmed: var(--uc-arrowpanel-dimmed) !important;
  --arrowpanel-dimmed-further: var(--uc-arrowpanel-dimmed-further) !important;
  --arrowpanel-dimmed-even-further: var(
    --uc-arrowpanel-dimmed-even-further
  ) !important;
  --desaturate-faint: var(--uc-desaturate-faint);
  --desaturate-dimmed: var(--uc-desaturate-dimmed);
  --desaturate-dimmed-further: var(--uc-desaturate-dimmed-further);
  --desaturate-dimmed-even-further: var(--uc-desaturate-dimmed-even-further);
  --panel-description-color: hsl(235, 48%, 89%, 70%) !important;
  --panel-shortcut-color: hsl(235, 27%, 73%, 70%) !important;
  --panel-shortcut-focus: hsl(235, 32%, 75%, 90%) !important;
  --panel-disabled-color: var(--context-menu-disabled-color) !important;
  --panel-separator-color: var(--desaturate-dimmed) !important;
  --toolbarbutton-hover-background: hsla(236, 82%, 83%, 0.17) !important;
  --toolbarbutton-active-background: hsla(236, 82%, 83%, 0.22) !important;
  --toolbarbutton-checked-background: hsla(236, 82%, 83%, 0.13) !important;
  --desaturate-hover-background: hsla(236, 60%, 83%, 0.17);
  --desaturate-active-background: hsla(236, 60%, 83%, 0.22);
  --desaturate-checked-background: hsla(236, 60%, 83%, 0.13);
  --button-bgcolor: var(--in-parent-button-background) !important;
  --button-hover-bgcolor: var(--in-parent-button-background-hover) !important;
  --button-active-bgcolor: var(--in-parent-button-background-active) !important;
  --button-primary-bgcolor: var(--purple-45) !important;
  --button-primary-hover-bgcolor: var(--purple-40) !important;
  --button-primary-active-bgcolor: var(--purple-35) !important;
  --button-primary-color: var(--button-color) !important;
  --urlbar-toolbar-height: var(--uc-urlbar-container-height) !important;
  --urlbar-container-height: var(--uc-urlbar-container-height) !important;
  --urlbar-height: calc(var(--urlbar-container-height) - 6px) !important;
  --urlbar-container-padding: 1px !important;
  --urlbar-icon-border-radius: var(--general-button-border-radius) !important;
  --urlbar-icon-fill-opacity: 0.6 !important;
  --identity-box-label-opacity: var(--urlbar-icon-fill-opacity) !important;
  --urlbar-popup-url-color: var(--purple-35) !important;
  --urlbar-popup-action-color: var(--pink-action) !important;
  --urlbarView-item-inline-padding: 8px !important;
  --tab-min-height: var(--uc-tab-min-height) !important;
  --tab-block-margin: 0px !important;
  --pinned-tab-width: 36px;
  --inline-tab-padding: 10px !important;
  --tab-shadow-max-size: 1px !important;
  --tabs-navbar-shadow-size: 1px !important;
  --autocomplete-popup-background: var(--arrowpanel-background) !important;
  --autocomplete-popup-color: var(--lwt-text-color) !important;
  --autocomplete-popup-separator-color: var(--panel-separator-color) !important;
  --autocomplete-popup-highlight-background: var(
    --arrowpanel-dimmed-further
  ) !important;
  --searchbar-popup-border: none;
  --searchbar-popup-border-radius: 0 0 4px 4px;
  --searchbar-popup-box-shadow: 0 0 10px 3px hsla(0, 0%, 0%, 0.15);
  --focus-outline-color: var(--attention-color) !important;
  --focus-outline-width: 2px;
  --places-cell-focus-bgcolor: hsl(232, 30%, 19%) !important;
  --places-cell-hover-bgcolor: hsl(232, 30%, 23%) !important;
  --ctrlTab-overlay-color: hsla(240, 3%, 4%, 0.5) !important;
  --ctrlTab-panel-disabled-bgcolor: hsla(240, 2%, 9%, 0.8) !important;
  --ctrlTab-panel-disabled-focused-bgcolor: hsla(240, 8%, 3%, 0.9) !important;
  --ctrlTab-panel-bgcolor: hsla(240, 3%, 4%, 0.9) !important;
  --ctrlTab-panel-focused-bgcolor: hsla(10, 12%, 2%, 0.99) !important;
  --in-content-box-background: hsl(232, 32%, 14%) !important;
  --in-content-box-border-color: rgba(249, 249, 250, 0.1) !important;
  --in-content-button-background: var(--arrowpanel-dimmed) !important;
  --in-content-button-background-hover: var(
    --arrowpanel-dimmed-further
  ) !important;
  --in-content-button-background-active: var(
    --arrowpanel-dimmed-even-further
  ) !important;
  --in-content-button-border-radius: var(
    --general-button-border-radius
  ) !important;
  --in-content-item-selected: var(--purple-40) !important;
  --in-content-border-highlight: var(--purple-40) !important;
  --in-content-border-focus: var(--purple-40) !important;
  --in-content-border-active: var(--purple-40) !important;
  --in-content-border-active-shadow: var(--purple-40-a30) !important;
  --in-content-category-outline-focus: 1px solid transparent !important;
  --in-content-link-color: var(--purple-35) !important;
  --in-content-link-color-hover: var(--purple-30) !important;
  --in-content-link-color-active: var(--purple-30) !important;
  --in-content-primary-button-background: var(--purple-40) !important;
  --in-content-primary-button-background-hover: var(--purple-35) !important;
  --in-content-primary-button-background-active: var(--purple-30) !important;

  --menu-background-color: var(--arrowpanel-background) !important;
  --menu-color: var(--ui-text-80) !important;
  --menuitem-hover-background-color: var(--arrowpanel-dimmed) !important;
  --menuitem-disabled-hover-background-color: initial !important;
  --menu-disabled-color: var(--panel-disabled-color) !important;
  --menu-border-color: var(--uc-menu-border-color) !important;
  --menu-icon-opacity: 1 !important;
  --panel-separator-margin: 4px !important;

  --panelview-toolbarbutton-hover-bgcolor: var(--arrowpanel-dimmed) !important;
  --panelview-toolbarbutton-active-bgcolor: var(
    --arrowpanel-dimmed-further
  ) !important;
  --panelview-toolbarbutton-focus-box-shadow: none !important;
  --buttons-destructive-bgcolor: hsl(350.1, 82%, 56.2%) !important;
  --buttons-destructive-hover-bgcolor: hsl(346, 86%, 46.6%) !important;
  --buttons-destructive-active-bgcolor: hsl(345.8, 90%, 40%) !important;
  --buttons-destructive-color: #fff !important;
  --panel-banner-item-color: HSL(240, 9.1%, 97.8%);
  --panel-banner-item-background-color: hsla(109.9, 90.9%, 47.3%, 0.1);
  --panel-banner-item-hover-bgcolor: hsla(109.9, 90.9%, 47.3%, 0.15);
  --panel-banner-item-active-bgcolor: hsla(109.9, 90.9%, 47.3%, 0.2);

  --arrowpanel-menuitem-inline-margin: 4px;
  --arrowpanel-menuitem-margin: 0px var(--arrowpanel-menuitem-inline-margin) !important;
  --arrowpanel-menuitem-padding: 6px !important;
  --arrowpanel-padding: 12px !important;
  --panel-subview-body-padding-magnitude: var(
    --arrowpanel-menuitem-inline-margin
  );
  --panel-subview-body-padding: var(--panel-subview-body-padding-magnitude) 0 !important;
  --arrowpanel-menuitem-border-radius: var(
    --context-menuitem-border-radius
  ) !important;
  --arrowpanel-border-radius: var(--menupopup-border-radius) !important;
  --panelui-subview-transition-duration: 200ms !important;

  --error-text-color: var(--warning-color) !important;
  --buttons-primary-button-bgcolor: var(
    --in-parent-primary-button-background
  ) !important;
  --buttons-primary-button-hover-bgcolor: var(
    --in-parent-primary-button-background-hover
  ) !important;
  --buttons-primary-button-active-bgcolor: var(
    --in-parent-primary-button-background-active
  ) !important;
  --buttons-primary-button-color: var(
    --in-parent-primary-button-text-color
  ) !important;
  --buttons-secondary-hover-bgcolor: var(--arrowpanel-dimmed) !important;
  --buttons-secondary-active-bgcolor: var(
    --arrowpanel-dimmed-further
  ) !important;

  --vertical-tabs-pane-background: var(--ui-secondary-bg) !important;
  --vpn-on-icon: url(chrome://userchrome/content/skin/vpn-on-light.svg);
}

:root[lwthemetextcolor="bright"] {
  --toolbarbutton-icon-fill: var(--lwt-toolbarbutton-icon-fill) !important;
  --vpn-on-icon: url(chrome://userchrome/content/skin/vpn-on-dark.svg);
}

:root:-moz-window-inactive:not([customizing]) {
  --urlbar-icon-fill-opacity: 0.4 !important;
}

toolbar {
  --toolbarbutton-hover-background: hsla(236, 82%, 83%, 0.17) !important;
  --toolbarbutton-active-background: hsla(236, 82%, 83%, 0.22) !important;
  --toolbarbutton-checked-background: hsla(236, 82%, 83%, 0.13) !important;
}

#urlbar {
  --urlbar-height: calc(var(--urlbar-container-height) - 6px) !important;
}

#confirmation-hint {
  --arrowpanel-background: var(--confirmation-hint-bgcolor) !important;
  --arrowpanel-color: var(--confirmation-hint-color) !important;
  --arrowpanel-border-color: var(--confirmation-hint-border-color) !important;
}

#browser {
  --sidebar-border-color: var(--uc-menu-border-color) !important;
}

.tab-loading-burst {
  --tab-loading-fill: var(--lwt-toolbarbutton-icon-fill-attention) !important;
}

/* fluent reveal toolbar buttons */
:root[fluent-reveal-hover]
  #navigator-toolbox
  :is(.toolbarbutton-1, .bookmark-item) {
  --toolbarbutton-hover-background: hsla(236, 100%, 69%, 0.05) !important;
  --toolbarbutton-active-background: hsla(236, 100%, 71%, 0.1) !important;
}

:root,
menupopup,
#TabsToolbar {
  --toolbarbutton-inner-padding: 6px !important;
}

#urlbar-container,
#urlbar {
  --urlbar-toolbar-height: var(--uc-urlbar-container-height) !important;
  --urlbar-container-height: var(--uc-urlbar-container-height) !important;
}

#appMenu-multiView {
  --arrowpanel-menuitem-inline-margin: 4px;
  --arrowpanel-menuitem-margin: 0px var(--arrowpanel-menuitem-inline-margin) !important;
  --arrowpanel-menuitem-padding: 8px !important;
  --subviewbutton-height: 32px !important;
  --panel-subview-body-padding-magnitude: var(
    --arrowpanel-menuitem-inline-margin
  );
  --panel-subview-body-padding: var(--panel-subview-body-padding-magnitude) 0 !important;
}
