}

/* Show Tab Close buttons only when hovered (slide in using max-width) */

.tabbrowser-tab:not([pinned]) .tab-close-button {
  min-width: 20px !important;
}

.tabbrowser-tab:not([pinned]):hover .tab-close-button {
