  background: transparent !important;
}

/*VIBRANCY (OLDer from reddit)*/

#TabsToolbar,
.tab-background,
#nav-bar,
.theme-dark,
.theme-light,
slider,
.CodeMirror,
.devtools-toolbar,
.message,
.theme-body,
.table-widget-empty-text {
  -moz-appearance: -moz-mac-vibrant-titlebar-dark !important;
}

/**----------------------------------------------------------------*/
#TabsToolbar {
  position: relative;
  z-index: 1;
}

#TabsToolbar::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.3) !important;
  background: transparent !important;
  background-image: linear-gradient(
    rgba(0, 0, 0, 0.1),
    rgba(0, 0, 0, 0.3)
  ) !important;
