    /* ---------------------------------------- Tabs (SVG) ---------------------------------------- */

    :root {
      @media (-moz-bool-pref: "userChrome.Tabs.Option1.Enabled") {
        --left-svg: url("special/tabs/option_1/left_corners.svg");
