    opacity: 0.6;
    position: absolute;
    top: calc(50% - 10px);
    pointer-events: none !important;
  }

  @media (-moz-platform: macos) {

    /*make space in nav bar for the private window label*/
    #nav-bar {
      padding-right: 70px !important;

      &::after {
        /*priv win label*/
        right: 8px;
        border-radius: 4px;
      }
    }
  }
}

