See the above repository for updates as well as full license text. */

/* Show tab close button when cursor is over the tab icon */

/* inline_tab_audio_icons.css is recommended because otherwise you cannot mute the tab using the mute button */

.tab-content {
  pointer-events: none
}

.tab-icon-image:not([busy]) {
  display: block !important;
}

:where(.tab-content:hover) .tab-icon-image,
:where(.tab-content:hover)>.tab-icon-stack {
  visibility: hidden;
}

.tab-close-button {
  order: -1;
  display: flex !important;
  position: relative;
  margin-inline: -4px -20px !important;
  padding-inline-start: 7px !important;
  opacity: 0;
  width: unset !important;
  pointer-events: auto;
}

.tab-close-button:hover {
  opacity: 1
}

.tabbrowser-tab[pinned] .tab-close-button {
  display: none !important;
}


/* reduce scrollbuttons size */
#scrollbutton-up,
#scrollbutton-down {
  padding: 0 !important;
  border-radius: 0 !important;
}

/*tab line for [selected] and [multiselected] tabs */
.tabbrowser-tab .tab-context-line {
  height: 2px !important;
  border-radius: 0px !important;
}

.tabbrowser-tab:is([selected], [multiselected]):not([usercontextid]) .tab-context-line {
  background-color: Highlight !important;
}

.tabbrowser-tab:not([selected])[multiselected] .tab-context-line {
  background-color: color-mix(in srgb, Highlight, white 40%) !important;
}

/* border between navigation toolbar and tabs toolbar */
:root:not([lwtheme]) #nav-bar {
  box-shadow: 0 calc(-1 * var(--tabs-navbar-shadow-size)) 0 color-mix(in srgb, currentColor 20%, transparent) !important;
}

:root[lwtheme] #nav-bar {
  box-shadow: unset !important;
}

:root[lwtheme] .tab-background:is([selected], [multiselected]),
:root[lwtheme] .tab-background:is([selected], [multiselected]) {
  box-shadow: inset -1px 0 0 0 rgba(255, 255, 255, .01),
    inset 1px 0 0 0 rgba(255, 255, 255, .01) !important;
}

.tabbrowser-tab>.tab-stack>.tab-background:not([selected])[multiselected] {
  background-color: color-mix(in srgb, currentColor 11%, transparent) !important;
}

.tabbrowser-tab:hover>.tab-stack>.tab-background:not([selected])[multiselected] {
  background-color: color-mix(in srgb, currentColor 16%, transparent) !important;
}

/* Fx 100+ outline fix */
:root[lwtheme] #TabsToolbar #firefox-view-button[open]>.toolbarbutton-icon,
:root[lwtheme] .tab-background[selected] {
  outline: unset !important;
}

/* Fx 101+ */
@media (-moz-platform: windows-win10),
(-moz-os-version:windows-win10) {

  #main-window[sizemode="maximized"][tabsintitlebar] #titlebar {
    margin-top: -3px !important;
  }
}

/* Fx 106+ */
#private-browsing-indicator-with-label {
  display: none !important;
}

/* Fx 110+ - remove line below active tab */
#nav-bar:not([tabs-hidden="true"]) {
  position: unset !important;
}

/* fix jumping pinned tabs / tabs box */
#TabsToolbar #tabbrowser-tabs[overflow] .tabbrowser-tab[pinned] {
  min-height: calc(var(--tab-min-height) - 2px) !important;
  max-height: calc(var(--tab-min-height) - 2px) !important;
}

#tabbrowser-tabs[overflow] .tabbrowser-tab[pinned] :is(.tab-background, .tab-stack) {
  min-height: calc(var(--tab-min-height) - 0px) !important;
}


#TabsToolbar .titlebar-spacer[type="pre-tabs"],
#TabsToolbar .titlebar-spacer[type="post-tabs"] {
  display: none !important;
}

#main-window:-moz-window-inactive #titlebar {
  opacity: 1.0 !important;
}

.tabbrowser-tab:not(:hover)>.tab-stack>.tab-background:not([selected], [multiselected]) {
  background-color: color-mix(in srgb, currentColor 11%, transparent);
}

.tabbrowser-tab:hover>.tab-stack>.tab-background:not([selected], [multiselected]) {
  background-color: color-mix(in srgb, currentColor 17%, transparent) !important;
}

.tabbrowser-tab {
  padding-inline: 1px !important;
}

#tabbrowser-tabs[haspinnedtabs]:not([positionpinnedtabs])>#tabbrowser-arrowscrollbox>.tabbrowser-tab:nth-child(1 of :not([pinned], [hidden])),
#tabbrowser-tabs[haspinnedtabs]:not([positionpinnedtabs])>#tabbrowser-arrowscrollbox>.tabbrowser-tab[first-visible-unpinned-tab] {
  margin-inline-start: 0px !important;
}

/* Blend Inactive tabs favicon */
#TabsToolbar #tabbrowser-tabs .tabbrowser-tab[pending] .tab-content:not(:hover) .tab-icon-image {
  filter: grayscale(70%);
  transition: filter 0.2s ease-in;
  opacity: 0.7;
}

/* Fx58+ titlebar placeholders */
#TabsToolbar .titlebar-placeholder[type="pre-tabs"],
#TabsToolbar .titlebar-placeholder[type="post-tabs"] {
  min-width: 0 !important;
  width: 0 !important;
  max-width: 0 !important;
}

#TabsToolbar .titlebar-placeholder[type="caption-buttons"] {
  margin-inline-start: 2px !important;
}

/* tab toolbar position */
#TabsToolbar {
  padding-right: 2px !important;
  padding-left: 2px !important;
  min-height: unset !important;
}

